import React, { Component } from 'react';
import { Col, Navbar } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Logo from './../img/Logo.png';
import './../../node_modules/bootstrap/dist/css/bootstrap.css'
import './HeaderBar.css'

class HeaderBar extends Component {

  constructor() {
    super();
    this.titleRef = React.createRef();
  }

  componentDidMount() {
    window.scrollTo({
      top: this.titleRef.current.offsetTop,
      behavior: "smooth"   // Optional, adds animation
    })
  }

  render() {
    return (
      <Navbar className="header-bar" style={{ width: "100vw" }}>
        <Col md={2} className="header-link">
          <Link to="/" >About</Link>
        </Col>
        <Col md={2} className="header-link">
          <Link to="/" >Software</Link>
        </Col>
        <Col md={2} className="header-link">
          <Link to="/" >Hardware</Link>
        </Col>
        <Col md={2} className="header-link">
          <Link to="/" >Writing</Link>
        </Col>
        <Col md={2} className="header-link">
          <Link to="/" >Volunteering</Link>
        </Col>
        <Col md={2} className="header-link">
          <Link to="/" >Contact</Link>
        </Col>
        <Col md={12}>
          <h3 ref={this.titleRef} className="page-title"> {this.props.title} </h3>
          <img style={{ width: "100px" }} src={Logo} />
        </Col>
      </Navbar>
    );
  }
}

export default HeaderBar;
