import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { Col, Navbar } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Logo from './../img/Logo.png';
import './../../node_modules/bootstrap/dist/css/bootstrap.css'
import './HeaderBar.css'

class HeaderBar extends Component {

  constructor() {
    super();
  }

  componentDidMount() {
    this.titleRef.click();
    window.scrollTo({ top: event.target.getBoundingClientRect().top, behavior: "smooth" })
  }

  render() {
    return (
      <div className="header-bar">
        <Navbar className="header-bar nav" style={{ width: "100vw" }}>
          <Col md={2} className="header-link">
            <Link to="/" >About</Link>
          </Col>
          <Col md={2} className="header-link">
            <Link to="/" >Software</Link>
          </Col>
          <Col md={2} className="header-link">
            <Link to="/" >Hardware</Link>
          </Col>
          <Col md={2} className="header-link">
            <Link to="/" >Writing</Link>
          </Col>
          <Col md={2} className="header-link">
            <Link to="/" >Volunteering</Link>
          </Col>
          <Col md={2} className="header-link">
            <Link to="/" >Contact</Link>
          </Col>
        </Navbar>
        <Col md={12}>
            <h3 id="page-title" onClick={(event) => { window.scrollTo({ top: event.target.getBoundingClientRect().top, behavior: "smooth" }) }} ref={titleRef => this.titleRef = titleRef}>
              {this.props.title}
            </h3>
          <img style={{ width: "100px" }} src={Logo} />
        </Col>
      </div>
    );
  }
}

export default HeaderBar;
